SS_FileView.MIN_SCALE = 0.1;
SS_FileView.MAX_SCALE = 2;
SS_FileView.SCALE_STEPS = [ 0.1, 0.25, 0.5, 0.75, 1, 1.25, 1.5, 1.75, 2.0 ];

