this.SS_AjaxFile = (function () {
  function SS_AjaxFile(el, options) {
    this.$el = $(el || "#ajax-file-box");
    this.options = options || {};

    this.render();
  }

  SS_AjaxFile.additionalFileResizings = [];
  SS_AjaxFile.firesEvents = false;

  SS_AjaxFile.errors = {
    entityTooLarge: "request entity is too large"
  }

  SS_AjaxFile.addFileResizing = function() {
    for (var i = 0; i < arguments.length; i++) {
      var argument = arguments[i];
      if (argument.default) {
        for (var j = 0; j < SS_AjaxFile.additionalFileResizings.length; j++) {
          if (SS_AjaxFile.additionalFileResizings[j].default) {
            SS_AjaxFile.additionalFileResizings[j].default = false;
          }
        }
      }

      SS_AjaxFile.additionalFileResizings.push(argument);
    }
  }

  SS_AjaxFile.defaultFileResizing = function () {
    for (var i = 0; i < SS_AjaxFile.additionalFileResizings.length; i++) {
      if (SS_AjaxFile.additionalFileResizings[i].default) {
        return SS_AjaxFile.additionalFileResizings[i].value;
      }
    }

    return null;
  };

  SS_AjaxFile.defaultFileSelectHandler = function() {
    var promisses = [];
    var fileViews = [];

    for (var i = 0; i < arguments.length; i++) {
      var $argument = $(arguments[i]);
      var url = $argument.attr('href');
      if (!url) {
        continue;
      }

      var filename = $argument.data("humanized-name");

      promisses.push($.ajax({
        url: url,
        success: function(html) {
          fileViews.push({ name: filename, html: html });
        },
        error: function(xhr, status, data) {
          if (xhr.responseJSON && Array.isArray(xhr.responseJSON)) {
            alert(["== Error =="].concat(xhr.responseJSON).join("\n"));
          } else {
            alert(["== Error =="].concat(xhr.statusText).join("\n"));
          }
        }
      }));
    }

    $.when.apply($, promisses).done(function () {
      fileViews.sort(function(a,b) {
        if (a.name < b.name) return 1;
        if (a.name > b.name) return -1;
        return 0;
      });

      for (var i = 0; i < fileViews.length; i++) {
        $("#selected-files").prepend(fileViews[i].html);
      }

      $.colorbox.close();
    });
  };

  SS_AjaxFile.prototype.render = function() {
    var self = this;

    self.$el.on("submit", "form.user-file", function (ev) {
      var submitted = "attach";
      if (ev.originalEvent && ev.originalEvent.submitter) {
        submitted = ev.originalEvent.submitter.dataset.submitted;
      }
      var $form = $(this);

      var params = {
        url: $form.attr("action") + ".json",
        dataType: "json",
        success: function(data, textStatus, xhr) {
          self.submitSuccess(submitted, data);
        },
        error: function (xhr, status, error) {
          self.submitError(xhr);
        },
        complete: function (xhr, status) {
          SS.enableFormElementsOnTimeoutSubmit();
        }
      };

      $form.ajaxSubmit(params);
      ev.preventDefault();
    });

    self.$el.on("click", ".user-files .select", function(ev) {
      self.selectFiles(this);

      ev.preventDefault();
      return false;
    });

    self.$el.on("click", ".user-files .delete", function(ev) {
      self.deleteFile(ev.target);

      ev.preventDefault();
      return false;
    });

    for (var i = 0; i < SS_AjaxFile.additionalFileResizings.length; i++) {
      var fileResizing = SS_AjaxFile.additionalFileResizings[i];
      var option = $('<option />').val(fileResizing.value).text(fileResizing.label);
      if (fileResizing.default) {
        option.prop('selected', true)
      }

      self.$el.find('form.user-file .image-size').append(option);
    }
  };

  SS_AjaxFile.prototype.submitSuccess = function(submitted, data) {
    var self = this;
    var loadUrl = (submitted === "attach") ? self.options.selectedFilesPath : self.options.indexPath;
    var indexOptions = self.options.pathOptions || {};

    if (submitted === "attach" && Array.isArray(data)) {
      var select_ids = $.map(data, function(v) { return v._id });
      indexOptions = Object.assign(indexOptions, { select_ids: select_ids });
    }

    if (Object.keys(indexOptions).length) {
      loadUrl += "?" + $.param(indexOptions);
    }

    $("<div />").load(loadUrl, function () {
      var $userFiles = $(this).find(".user-files");
      if ($userFiles[0]) {
        // TODO:  insert 
        self.$el.find(".user-files").html($userFiles.html());
        SS.renderInBox(self.$el);
      }

      self.$el.find("form.user-file [type='file']").val(null).trigger("change");

      var defaultImageSize = null;
      for (var i = 0; i < SS_AjaxFile.additionalFileResizings.length; i++) {
        var fileResizing = SS_AjaxFile.additionalFileResizings[i];
        if (fileResizing.default) {
          defaultImageSize = fileResizing.value;
          break;
        }
      }
      self.$el.find("form.user-file .image-size").val(defaultImageSize).trigger("change");

      if (submitted === "attach") {
        self.attachFiles(data);
      } else {
        $.rails.enableFormElements(self.$el.find('form.user-file'));
      }
    });
  };

  SS_AjaxFile.prototype.attachFiles = function(data) {
    var self = this;

    if (!Array.isArray(data) || data.length == 0) {
      return;
    }

    var elements = [];
    $.each(data, function(index, file) {
      var item = self.$el.find(".user-files [data-id='" + file._id + "'] .select");
      elements.push(item);
    });

    self.selectFiles.apply(self, elements);
  };

  SS_AjaxFile.prototype.submitError = function(xhr) {
    var self = this;
    if (xhr.status === 413) {
      alert(["== Error =="].concat(SS_AjaxFile.errors.entityTooLarge).join("\n"));
    } else {
      try {
        alert(["== Error =="].concat(xhr.responseJSON).join("\n"));
      } catch(_error) {
        alert(["== Error =="].concat(xhr.statusText).join("\n"));
      }
    }

    $.rails.enableFormElements(self.$el.find('form.user-file'));
  };

  SS_AjaxFile.prototype.selectFiles = function() {
    if (SS_SearchUI.anchorAjaxBox) {
      if (SS_AjaxFile.firesEvents) {
        var event = $.Event("ss:ajaxFileSelected");
        SS_SearchUI.anchorAjaxBox.trigger(event, [ arguments ]);
        if (event.isDefaultPrevented()) {
          return;
        }
      }

      var handler = SS_SearchUI.anchorAjaxBox.data('on-select');
      if (handler) {
        $.each(arguments, function() {
          handler($(this));
        });

        return true;
      }
    }

    SS_AjaxFile.defaultFileSelectHandler.apply(SS_AjaxFile, arguments);
  };

  SS_AjaxFile.prototype.deleteFile = function(el) {
    if (!confirm(i18next.t('ss.confirm.delete'))) {
      return false;
    }

    var $el = $(el);
    $.ajax({
      type: "POST",
      data: "_method=delete",
      url: $el.attr("href") + ".json",
      dataType: "json",
      beforeSend: function () {
        $el.html(SS.loading);
      },
      success: function () {
        var $target;
        if ($el.data("remove")) {
          $target = $($el.data("remove"));
        } else {
          $target = $el.closest(".file-view")
        }

        $target.slideUp("fast", function() {
          $target.remove();
          if (SS_AjaxFile.firesEvents && SS_SearchUI.anchorAjaxBox) {
            SS_SearchUI.anchorAjaxBox.trigger("ss:ajaxRemoved");
          }
        });
      },
      error: function (data, status) {
        alert(["== Error =="].concat(data.responseJSON).join("\n"));
      }
    });
  };

  return SS_AjaxFile;

})();
this.Chat_Bot = (function () {
  function Chat_Bot(el, url) {
    this.$el = $(el);
    this.url = url;
    this.inProgress = false;
    this.render();
  }

  Chat_Bot.prototype.render = function () {
    var _this = this;
    _this.$el.on("keypress", '.chat-text', function (ev) {
      if ((ev.which && ev.which === SS.KEY_ENTER) || (ev.keyCode && ev.keyCode === SS.KEY_ENTER)) {
        _this.sendChatRequest($(this));
      }
    });
    _this.$el.on("click", '.chat-button', function () {
      _this.sendChatRequest($(this));
    });
    _this.$el.on("click", '.chat-items', function (ev) {
      var $target = $(ev.target);
      if ($target.hasClass('chat-suggest')) {
        _this.sendChatRequest($(this), { text: $target.text(), clickSuggest: true });
        return false;
      } else if ($target.hasClass('chat-success')) {
        _this.sendFeedback($target, 'success');
        return false;
      } else if ($target.hasClass('chat-retry')) {
        _this.sendFeedback($target, 'retry');
        return false;
      }
    });
    _this.$el.on("click", '.chat-dismiss', function () {
      _this.$el.trigger("ss:chatClose");
    });
  };

  Chat_Bot.prototype.fetchFirstMessagenAndSuggestions = function () {
    var _this = this;

    if (_this.inProgress) {
      return;
    }

    _this.inProgress = true;

    $.ajax({
      type: "GET",
      url: _this.url,
      cache: false,
      success: function (res, _status) {
        _this.renderChatResponse(res);
      },
      error: function (xhr, status, error) {
      },
      complete: function(xhr, status) {
        _this.inProgress = false;
      }
    });
  };

  Chat_Bot.prototype.sendChatRequest = function ($el, options) {
    var _this = this;
    if (_this.inProgress) {
      return;
    }

    _this.inProgress = true;

    var $chatText = _this.$el.find('.chat-text');

    var text;
    if (options) {
      text = options.text;
    }
    if (!text) {
      text = $chatText.val();
      $chatText.val('');
    }
    if (!text) {
      return false;
    }

    var $chatItems = _this.$el.find('.chat-items');
    $chatItems
      .append($('<div class="chat-item user"></div>').append(text))
      .animate({ scrollTop: $chatItems[0].scrollHeight });
    $.ajax({
      type: "GET",
      url: _this.url,
      cache: false,
      data: {
        text: text,
        click_suggest: options && options.clickSuggest
      },
      success: function (res, status) {
        _this.renderChatResponse(res);
      },
      error: function (xhr, status, error) {
      },
      complete: function(xhr, status) {
        _this.inProgress = false;
      }
    });
  };

  Chat_Bot.prototype.renderChatResponse = function (res) {
    var _this = this;
    var $chatItems = _this.$el.find('.chat-items');

    var result = res;
    if (typeof res === 'string' || res instanceof String) {
      result = $.parseJSON(res);
    }

    $.each(result.results, function (i, r) {
      if (!r.response) {
        return;
      }

      var siteSearchParagraph;
      if (r.siteSearchUrl) {
        var siteSearchLink = $('<a />', { href: r.siteSearchUrl, target: "_blank", rel: "noopener" }).append(result.siteSearchText);
        siteSearchParagraph = $('<p />', { class: "search-result-btn" }).append(siteSearchLink);
      } else {
        siteSearchParagraph = '';
      }

      if (r.suggests) {
        $chatItems.append($('<div class="chat-item sys"></div>').append(r.response).append(siteSearchParagraph));
        r.suggests.forEach(function (suggest) {
          var chatSuggest = $('<a class="chat-suggest"></a>').attr('href', _this.url).append(suggest);
          $chatItems.append($('<div class="chat-item suggest"></div>').append(chatSuggest));
        });
      } else if (r.question) {
        var chatSuccess = $('<button name="button" type="button" class="chat-success" data-id="' + r.id + '"></button>').append(result.chatSuccess);
        var chatRetry = $('<button name="button" type="button" class="chat-retry" data-id="' + r.id + '"></button>').append(result.chatRetry);
        var chatFinish = $('<div class="chat-finish"></div>').append(r.question).append(chatSuccess).append(chatRetry);
        $chatItems.append($('<div class="chat-item sys"></div>').append(r.response).append(siteSearchParagraph).append(chatFinish));
      } else {
        $chatItems.append($('<div class="chat-item sys"></div>').append(r.response).append(siteSearchParagraph));
      }
    });

    $chatItems.animate({ scrollTop: $chatItems[0].scrollHeight });
  };

  Chat_Bot.prototype.sendFeedback = function ($el, question) {
    var _this = this;
    if (_this.inProgress) {
      return;
    }

    _this.inProgress = true;

    $.ajax({
      type: "GET",
      url: _this.url,
      cache: false,
      data: {
        intent_id: $el.data('id'),
        question: question || 'success'
      },
      success: function (res, _status) {
        _this.renderFeedback(res);
      },
      error: function (xhr, status, error) {
      },
      complete: function(xhr, status) {
        _this.inProgress = false;
      }
    });
  };

  Chat_Bot.prototype.renderFeedback = function (res) {
    var _this = this;

    var result = res;
    if (typeof res === 'string' || res instanceof String) {
      result = $.parseJSON(res);
    }

    var $chatItems = _this.$el.find('.chat-items');

    $.each(result.results, function (i, r) {
      if (!r.response) {
        return;
      }

      $chatItems.append($('<div class="chat-item sys"></div>').append(r.response));
    });

    $chatItems.animate({ scrollTop: $chatItems[0].scrollHeight });
    setTimeout(_this.fetchFirstMessagenAndSuggestions.bind(_this), 1000)
  };

  return Chat_Bot;

})();
