        visible = false;
        category = this.get("category");
        $.each(dataIds, function () {
          if ($.inArray(parseInt(this), category) >= 0) {
            visible = true;
            return false;
          }
        });
        dataId = this.get("markerId");
        column = $('#map-sidebar .column[data-id="' + dataId + '"]');
        map.popup.hide();
