  // $.isEmptyObject({})        | true      | true   |
  // $.isEmptyObject(0)         | true      | true   |
  // $.isEmptyObject("")        | true      | false  |
  // $.isEmptyObject([0])       | false     | false  |
  // $.isEmptyObject({a:0})     | false     | false  |
  // $.isEmptyObject(1)         | true      | true   |
  // $.isEmptyObject(-1)        | true      | true   |
